// MessageItem.js

const MessageItem = ({ message, isOwnMessage }) => {
    const messageClass = isOwnMessage ? "message-own" : "message-other";
    return (
        <div className={`message-item ${messageClass}`}>
            <span>{message.text}</span>
        </div>
    );
};

export default MessageItem;
