import React from 'react';
import APIResponseErrorMessage from "../commons/errorhandling/api-response-error-message";
import {
    Button,
    Card,
    CardHeader,
    Col,
    Modal,
    ModalBody,
    ModalHeader,
    Row
} from 'reactstrap';

import * as API_EM from "./api/em-api"
import EnergyMeterTable from './components/em-table';
import { Redirect } from 'react-router-dom';
import SockJS from 'sockjs-client';
import Stomp from 'stompjs';

const localStorage = window.localStorage;

class UserPage extends React.Component {

    constructor(props) {
        super(props);
        this.reload = this.reload.bind(this);
        this.state = {
            addFormSelected: false,
            updateFormSelected: false,
            deleteFormSelected: false,
            collapseForm: false,
            tableData: [],
            isLoaded: false,
            errorStatus: 0,
            error: null,
            notification: '',
            showModal: false,
            selectedDate: new Date(),
            energyData: [],
        };
        this.stompClient = null;
    }

    componentDidMount() {
        this.fetchPersons();
        this.connectWebSocket();
    }

    componentWillUnmount() {
        if (this.stompClient) {
            this.stompClient.disconnect();
        }
    }

    handleDateChange = (date) => {
        this.setState({
            selectedDate: date
        }, () => {
            this.fetchEnergyData();
        });
    };

    connectWebSocket() {
        const socket = new SockJS('http://localhost:8082/ws');
        this.stompClient = Stomp.over(socket);
        const currentUserId = localStorage.getItem('id');

        this.stompClient.connect({}, frame => {
            this.stompClient.subscribe('/topic/user.' + currentUserId, notification => {
                this.setState({
                    notification: notification.body,
                    showModal: true
                });

            });
        });
    }

    fetchPersons() {
        const userId = localStorage.id;

        return API_EM.getEnergyMetersByUserId(userId, (result, status, err) => {
            if (result !== null && status === 200) {
                console.log(result)
                this.setState({
                    tableData: result,
                    isLoaded: true
                });
            } else {
                this.setState(({
                    errorStatus: status,
                    error: err
                }));
            }
        });
    }

    fetchEnergyData = () => {
        const { selectedDate } = this.state;
        const userId = localStorage.getItem('id');
        const formattedDate = selectedDate.toISOString().split('T')[0]; // Format date as YYYY-MM-DD

        API_EM.getEnergyDataByUserIdAndDate(userId, formattedDate, (result, status, err) => {
            // Assume the API returns an array of objects with 'hour' and 'value' keys
            if (result !== null && status === 200) {
                this.setState({
                    energyData: result
                });
            } else {
                // Handle errors
            }
        });
    };

    reload() {
        this.setState({
            isLoaded: false
        });
        this.fetchPersons();
    }

    toggleModal = () => {
        this.setState({ showModal: !this.state.showModal });
    }

    render() {
        return (
            <div>
                {localStorage.role !== "ROLE_CLIENT" && <Redirect to="/login" push/>}

                <Modal isOpen={this.state.showModal} toggle={this.toggleModal}>
                    <ModalHeader toggle={this.toggleModal}>Notification</ModalHeader>
                    <ModalBody>
                        {this.state.notification}
                    </ModalBody>
                </Modal>

                <CardHeader>
                    <strong> Energy Meters </strong>
                </CardHeader>
                <Card>
                    <Row>
                        <Col sm={{size: '8', offset: 1}}>
                            {this.state.isLoaded && <EnergyMeterTable  tableData = {this.state.tableData}/>}
                            {this.state.errorStatus > 0 && <APIResponseErrorMessage
                                                            errorStatus={this.state.errorStatus}
                                                            error={this.state.error}
                                                        />   }
                        </Col>
                    </Row>
                </Card>
            </div>
        )

    }
}

export default UserPage;
