import React from 'react';
import ChatHeader from './chat-header';
import MessageList from './message-list';
import ChatInput from './chat-input';
import SockJS from 'sockjs-client';
import Stomp from 'stompjs';


class ChatWindow extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            stompClient: null,
            messages: [],
            sender: props.sender,
            receiver: props.receiver 
        };
    }

    componentDidMount() {
        // Initialize WebSocket connection
        const socket = new SockJS('http://localhost:8083/chat-socket');
        const client = Stomp.over(socket);

        client.connect({}, () => {
            this.setState({ stompClient: client });

            // Subscribe to receive messages
            client.subscribe('/topic/messages', (message) => {
                const newMessage = JSON.parse(message.body);
                this.setState(prevState => ({
                    messages: [...prevState.messages, newMessage]
                }));
            });
        });
    }

    componentWillUnmount() {
        // Disconnect the WebSocket
        if (this.state.stompClient) {
            this.state.stompClient.disconnect();
        }
    }

    sendMessage = (messageContent) => {
        const { stompClient, sender, receiver } = this.state; // Assuming sender and receiver are in state
        if (stompClient && stompClient.connected) {
            const chatMessage = {
                type: "CHAT", // Or another MessageType based on your logic
                content: messageContent,
                sender: sender, // Sender's identifier
                receiver: receiver, // Receiver's identifier
                // 'timestamp' will be set server-side
            };
            console.log(chatMessage)
            stompClient.send("/app/send-message", {}, JSON.stringify(chatMessage));
        }
    };

    render() {
        const { messages } = this.state;
        const { chatWith } = this.props;

        return (
            <div className="chat-window">
                <ChatHeader chatWith={chatWith} />
                <MessageList messages={messages} />
                <ChatInput onSendMessage={this.sendMessage} />
            </div>
        );
    }
}

export default ChatWindow;
