import React from 'react';
import ChatHeader from './chat-header';
import MessageList from './message-list';
import ChatInput from './chat-input';
import SockJS from 'sockjs-client';
import Stomp from 'stompjs';
import UserChatWindow from "./user-chat-window";
import UserList from "./user-list";

class AdminChatWindow extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            stompClient: null,
            messages: [],
            selectedUser: null,
            users: props.usersList
        };
    }

    onSelectUser = (user) => {
        this.setState({ selectedUser: user });
    };

    render() {
        const { selectedUser } = this.state;

        return (
            <div>
                <UserList users={users} onSelectUser={this.onSelectUser} />
                {selectedUser && <UserChatWindow receiver={selectedUser.username} sender={"admin"}/>}
            </div>
        );
    }
}

export default AdminChatWindow;
